package com.example.ch3_3_hw.repository;

import com.example.ch3_3_hw.model.Post;
import org.springframework.stereotype.Repository;

import java.util.ArrayList;
import java.util.List;

@Repository
public class DBManager implements PostRepository{
    private static List<Post> allPosts = new ArrayList<>();

    static {
        allPosts.add(new Post(1L, "УРААА!!!", "Вышел финальный сезон аниме Атака Титанов", "Вышел финальный сезон аниме Атака Титанов, я очень его ждала, это мое любимое аниме!", "https://shogi-pineapple.com/wp-content/uploads/2021/11/1636903487_Shingeki-no-Kyojin-The-Final-Season-revela-un-nuevo-visual.jpg", "02-14-2022"));
        allPosts.add(new Post(2L, "Kimetsu no Yaiba", "Второй сезон держит планку!", "Вы уже смотрели новые серии Клинка рассекающего демонов?", "data:image/jpeg;base64,", "02-15-2022"));
        allPosts.add(new Post(3L, "Эта фарфоровая кукла влюбилась", "Топовая рисовка и краш-персонажи", "ОБязательно к просмотру!!", "https://image.krasview.ru/video/aad73f6ae6125fb/__2.jpg","01-25-2022"));
        allPosts.add(new Post(4L, "LALALALAL", "Топовая рисовка и краш-персонажи", "ОБязательно к просмотру!!", "data:image/jpeg;base64,","01-25-2022"));

    }

    public static Long id = 6L;

    @Override
    public List<Post> getAllPosts() {
        return allPosts;
    }

    @Override
    public void addPost(Post post) {
        post.setId(id);
        allPosts.add(post);
        id++;
    }

    @Override
    public Post getPostById(Long id) {
        for (Post p : allPosts){
            if (p.getId().equals(id)){
                return p;
            }
        }
        return null;
    }
}
